import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, Button, Typography  } from '@mui/material';
import { makeStyles } from '@mui/st'

import ChatPage from './components/chatPage.component';

const useStyles = makeStyles((theme) => ({
  gradientBackground: {
    backgroundColor: '#8BC6EC',
    backgroundImage: `linear-gradient(135deg, #8BC6EC 0%, #9599E2 100%)`,
    height: '100vh', // Adjust the height as needed
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
}));

function App() {

  const classes = useStyles();

  return (
    <Box className={classes.gradientBackground} sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', p: 2}}>
      <Typography variant="h2" >Ask Your PDF</Typography>
    </Box>
    //<ChatPage />
  );
}

export default App;
