import React, { useState } from 'react';
import axios from 'axios';
import { Document, Page, pdfjs } from "react-pdf";
import { Box, TextField, Button, Typography } from '@mui/material';

function App() {
  const [pdfUrl, setPdfUrl] = useState('');
  const [query, setQuery] = useState('');
  const [chatResponse, setChatResponse] = useState('');

  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    const formData = new FormData();
    formData.append('pdf', file);




    try {
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      const documentId = response.data.sourceId;
      setPdfUrl(`http://localhost:5000/pdf/${documentId}`);
    } catch (error) {
      console.error(error);
    }
  };

  const handleQuerySubmit = async () => {
    try {
      const response = await axios.post('http://localhost:5000/chat', { query, sourceId: getDocumentIdFromUrl() });
      setChatResponse(response.data.text);
    } catch (error) {
      console.error(error);
    }
  };

  const getDocumentIdFromUrl = () => {
    const matches = pdfUrl.match(/\/pdf\/([^/]+)$/);
    return matches ? matches[1] : '';
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', p: 2 }}>
      <Box sx={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between', width: '100%', p: 2 }}>
        <Box sx={{ width: '50%' }}>
          <Box sx={{ display: 'flex', flexDirection: 'row', alignItems: 'center' }}>
            <Typography variant="h6">Upload PDF:</Typography>
            <input type="file" onChange={handleFileChange} />
          </Box>
          {pdfUrl && (
            <Box sx={{ width: '100%', height: '80vh', overflow: 'auto' }}>
              <Document file={pdfUrl}>
                <Page pageNumber={1} />
              </Document>
            </Box>
          )}
        </Box>
        <Box sx={{ width: '50%' }}>
          <Typography variant="h6">Chat with PDF:</Typography>
          <TextField
            variant="outlined"
            fullWidth
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Ask your question..."
          />
          <Button variant="contained" onClick={handleQuerySubmit} sx={{ mt: 1 }}>
            Ask
          </Button>
          {chatResponse && (
            <Box sx={{ mt: 2 }}>
              <Typography variant="body1">{chatResponse}</Typography>
            </Box>
          )}
        </Box>
      </Box>
    </Box>
  );
}

export default App;
