import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import { Document, Page, pdfjs } from 'react-pdf';
import { Box, Paper, Typography, TextField, Button } from '@mui/material';
import axios from 'axios';

const App = () => {
  const [query, setQuery] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [pdfUrl, setPdfUrl] = useState('');
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const socket = io('http://localhost:5000');

  useEffect(() => {
    socket.on('chat message', (message) => {
      setChatHistory([...chatHistory, message]);
    });
  }, [socket, chatHistory]);

  const handleChangeQuery = (event) => {
    setQuery(event.target.value);
  };

  const handleUploadPDF = async (event) => {
    const file = event.target.files[0];
    const formData = new FormData();
    formData.append('pdf', file);

    try {
      const response = await axios.post('http://localhost:3000/upload', formData);
      const documentId = response.data.documentId;
      setPdfUrl(`/pdf/${documentId}`);
    } catch (error) {
      console.error(error);
    }
  };


  const handleSubmit = (event) => {
    event.preventDefault();
    socket.emit('chat message', { query, documentId: pdfUrl.split('/')[2] });
    setQuery('');
  };

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <Box sx={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between', width: '100%', p: 2 }}>
        <Box sx={{ width: '50%' }}>
          <Box sx={{ display: 'flex', flexDirection: 'row', alignItems: 'center' }}>
            <Typography variant="h6">Upload PDF:</Typography>
            <input type="file" onChange={handleUploadPDF} />
          </Box>
          {pdfUrl && (
            <Box>
              <Document file={pdfUrl} onLoadSuccess={onDocumentLoadSuccess}>
                <Page pageNumber={pageNumber} width={300} />
              </Document>
              <p>
                Page {pageNumber} of {numPages}
              </p>
            </Box>
          )}
        </Box>
        <Box sx={{ width: '50%' }}>
          <Typography variant="h6">Chat with PDF:</Typography>
          <Paper sx={{ p: 2, height: '80vh', overflowY: 'auto' }}>
            <ul>
              {chatHistory.map((message, index) => (
                <li key={index}>
                  {message.isBot ? (
                    <Box sx={{ display: 'flex', flexDirection: 'row' }}>
                      <Typography variant="body1" sx={{ color: 'blue' }}>
                        ChatPDF:
                      </Typography>
                      <Typography variant="body1">{message.text}</Typography>
                    </Box>
                  ) : (
                    <Typography variant="body1">{message.text}</Typography>
                  )}
                </li>
              ))}
            </ul>
          </Paper>
          <TextField variant="outlined" fullWidth value={query} onChange={handleChangeQuery} placeholder="Ask your question..." />
          <Button variant="contained" onClick={handleSubmit} sx={{ mt: 1 }}>
            Ask
          </Button>
        </Box>
      </Box>
    </Box>
  );
};

export default App;
